export const ADD_NUMBER = 'ADD_NUMBER';
export const ADD_OPERATION = 'ADD_OPERATION';
export const RESTORE_EXPRESSTION = 'RESTORE_EXPRESSTION';
export const ADD_HISTORY = 'ADD_HISTORY';

export const toExpresstion = (type, payload) => (
    {
        type,
        payload
    }
)

export const numAdd = (number) => toExpresstion(ADD_NUMBER, number);
export const opeAdd = (operation) => toExpresstion(ADD_OPERATION, operation);
