import { createSlice } from "@reduxjs/toolkit";

// const initialValue = {
//     currentExpresstion: '',
//     history: [],
// }

const initialState = {
    value: 0
}

export const expresstionSlice = createSlice({
    name: 'expresstion',
    initialState,
    reducers: {
        ADD_NUMBER: (state) => {
            // state.currentExpresstion + action.payload
            state.value += action.payload
        },
        ADD_OPERATION: (state) => {
            // state.currentExpresstion + action.payload
            state.value -= 1
        },
        // RESTORE_EXPRESSTION: (state) => {
        //     state
