export const ADD_NUMBER = 'ADD_NUMBER';
export const ADD_OPERATION = 'ADD_OPERATION';
export const RESTORE_EXPRESSTION = 'RESTORE_EXPRESSTION';
export const ADD_HISTORY = 'ADD_HISTORY';

export const toExpresstion = (type, payload) => (
    {
        type,
        payload
    }
)

export const AddNumberToExpresstion = (number) => { console.log(number); toExpresstion(ADD_NUMBER, number) }
export const AddOperationToExpresstion = (operation) => toExpresstion(ADD_OPERATION, operation);

export const restoreExpression = (payload) => ({
    type: RESTORE_EXPRESSTION,
    payload,
});
