const ADD_NUMBER = 'ADD_NUMBER';
const ADD_OPERATION = 'ADD_OPERATION';
const RESTORE_EXPRESSTION = 'RESTORE_EXPRESSTION';
const ADD_HISTORY = 'ADD_HISTORY';

export const toExpresstion = (type, payload) => (
    {
        type,
        payload
    }
)

export const AddNumberToExpresstion = (number) => { toExpresstion('ADD_NUMBER', number) }
export const AddOperationToExpresstion = (operation) => toExpresstion('ADD_OPERATION', operation);

export const restoreExpression = (payload) => ({
    type: RESTORE_EXPRESSTION,
    payload,
});
