import { ADD_HISTORY, ADD_NUMBER, ADD_OPERATION, RESTORE_EXPRESSTION } from "../action";
export const initialState = {
    currentExpresstion: '',
    history: []
}


const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_NUMBER:
            return {
                ...state,
                currentExpresstion: state.currentExpresstion + action.payload
            }
        case ADD_OPERATION:
            return {
                ...state,
                currentExpresstion: state.currentExpresstion + action.payload
            }
        case ADD_HISTORY:
            return {
                ...state,
                history: [...state.history, action.payload],
            }
        case RESTORE_EXPRESSTION:
            return state;
        default:
            break;
    }
}

export default rootReducer;
