import { createSlice } from "@reduxjs/toolkit";

const initialValue = {
    currentExpresstion: '',
    history: [],
}

export const expresstionSlice = createSlice({
    name: 'expresstion',
    initialValue,
    reducers: {
        ADD_NUMBER: (state, action) => {
            state.currentExpresstion + action.payload
        },
        ADD_OPERATION: (state, action) => {
            state.currentExpresstion + action.payload
        },
        RESTORE_EXPRESSTION: (state, action) => {
            state
        },
        ADD_HISTORY: (state, action) => {
            state.history.push('ssss')
        }
    }
})

const expresstionReducer = expresstionSlice.reducers;

export default expresstionReducer;
