import { createSlice } from "@reduxjs/toolkit";

// const initialValue = {
//     currentExpresstion: '',
//     history: [],
// }

const initialValue = {
    value: 0
}

export const expresstionSlice = createSlice({
    name: 'expresstion',
    initialValue,
    reducers: {
        ADD_NUMBER: (state) => {
            // state.currentExpresstion + action.payload
            state.value += 1
        },
        ADD_OPERATION: (state) => {
            // state.currentExpresstion + action.payload
            state.value -= 1
        },
        // RESTORE_EXPRESSTION: (state) => {
        //     state
        // },
        // ADD_HISTORY: (state) => {
        //     state.history.push('ssss')
        // }
    }
})

const expresstionReducer = expresstionSlice.reducers;
console.log(expresstionSlice);

export default expresstionReducer;
