import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    currentExpresstion: '',
    history: [],
    resuilt: '',
}



export const expresstionSlice = createSlice({
    name: 'expresstion',
    initialState,
    reducers: {
        ADD_NUMBER: (state, action) => {
            state.currentExpresstion += action.payload
        },
        ADD_OPERATION: (state, action) => {
            state.currentExpresstion += action.payload
        },
        RESTORE_EXPRESSTION: (state, action) => {
            if (action.payload === 'AC') {
                state.currentExpresstion = ''
            } else if (action.payload === 'C') {
                state.currentExpresstion.substring(0, state.currentExpresstion.length - 1)
                console.log(state.currentExpresstion.substring(0, state.currentExpresstion.length - 1));
                console.log('delet one');
            } else if (action.payload === '=') {
                console.log('bawng');
            }
        },
        ADD_HISTORY: (state, action) => {
            state.history.push(action.payload)
        }
    }
})

export default expresstionSlice.reducer
